sys_script_client.script:
function onLoad() {
    if (g_user.hasRoleExactly('x_847080_e_learnin.student')) {
        g_form.setReadOnly('student', true);
        g_form.setReadOnly('teacher', true);
        g_form.setReadOnly('assignment_subject', true);
        g_form.setReadOnly('assignment_number', true);
        g_form.setReadOnly('instructions', true);
        g_form.setReadOnly('assignement_id', true);
        g_form.setReadOnly('total_marks', true);
        g_form.setReadOnly('assigned_date', true);
        g_form.setReadOnly('due_date', true);
        g_form.setReadOnly('marks_obtained', true);
        g_form.setReadOnly('remarks', true);
        if (g_form.getValue('u_correction_done') == 'false') {
            g_form.setDisplay('u_correction_done', false);
            g_form.setDisplay('marks_obtained', false);
            g_form.setDisplay('remarks', false);
        }
        g_form.setReadOnly('u_correction_done', true);
        g_form.setReadOnly('submission_status', true);
        g_form.setReadOnly('submission_date', true);
    }
    if (g_user.hasRoleExactly('x_847080_e_learnin.teacher')) {
        g_form.setReadOnly('student', true);
        g_form.setReadOnly('teacher', true);
        g_form.setReadOnly('assignment_number', true);
        g_form.setReadOnly('assignment_subject', true);
        g_form.setReadOnly('assignement_id', true);
        g_form.setReadOnly('total_marks', true);
        g_form.setReadOnly('assigned_date', true);
        g_form.setReadOnly('answer', true);
        g_form.setReadOnly('submission_status', true);
        g_form.setReadOnly('submission_date', true);
        g_form.setSectionDisplay('request', false);
    }
}
